value.paths:
EVALUATE: context(for tool in previous.tools return {
  key: "/" + tool.name,
  value: {
    post: {
      tags: [
          "service"
      ],
      "x-context": if tool.annotations.readOnlyHint = true then false else true,
      "x-events": [],
      summary: tool.annotations.title,
      description: tool.description,
      parameters: for parameter in get entries(tool.inputSchema.properties) return {
        name: parameter.key,
        required: list contains(tool.inputSchema.required, parameter.key),
        summary: parameter.key,
        schema: parameter.value
      }
    }
  }
})
